/**
 * 计算文件大小
 *
 * @access public
 * @param string $file 要计算大小的文件名
 * @return string
 */
function getSize( $file ) {
	$num = filesize( $file );
	clearstatcache();
	$sizestr = '';
	if ( $num < 1024 ) {
		$sizestr = $num . 'B';
	} elseif ( $num >= 1024 && $num < 1048576 ) {
		$sizestr = round( $num / 1024, 2 ) . 'K';
	} elseif ( $num >= 1048576 && $num < 1073741824 ) {
		$sizestr = round( $num / 1048576, 2 ) . 'M';
	} else {
		$sizestr = round( $num / 1073741824, 2 ) . 'G';
	}
	return $sizestr;
}

/**
