			case 'cut':
				foreach ( explode( ',', $opendfile ) as $v ) {
					$dirarr = explode( '/', $dir . '/' . $v );
					$targetdir = $dirarr[ count( $dirarr ) - 1 ];
					$msg .= fcut( $dir . '/' . $v, $extradata . '/', $targetdir );
				}
				break;
			case 'chmod':
